import UU5 from "uu5g04";

export const REGEXP = {
  fileExtension: /^[\w,\s-]+\.[A-Za-z]{3}$/
};

export const Tools = {
  fetchAuthenticatedImage: (imageURL, token) => {
    return UU5.Common.Request.get(imageURL, null, {
      headers: {
        Accept: "image/*,*/*;q=0.8",
        Authorization: token ? "Bearer " + token : undefined
      }
    });
  }
};

export default Tools;
