import EditorBlockLazy from "../../editor-block-lazy.js";

import uu5pluginDef from "../uu5/blockquote.js";

const pluginDef = uu5pluginDef
  .setIn(["toEditorItemDefs", "tagName"], "blockquote")
  .setIn(["toEditorItemDefs", "component"], properties => {
    return (
      <blockquote {...properties.blockProps}>
        <EditorBlockLazy {...properties} />
      </blockquote>
    );
  });

export default pluginDef;
