import uu5pluginDef from "../uu5/code.js";

const pluginDef = uu5pluginDef
  .setIn(["toEditorItemDefs", "tagName"], "code")
  .setIn(["toEditorItemDefs", "component"], properties => {
    const dataProps = properties.contentState.getEntity(properties.entityKey).getData();
    return <code {...dataProps}>{properties.children}</code>;
  });

export default pluginDef;
