import Immutable from "immutable";
import uu5pluginDef from "../uu5/index.js";

const pluginDef = uu5pluginDef
  .setIn(["toEditorItemDefs", "0", "tagName"], "span")
  .setIn(["toEditorItemDefs", "0", "inlineStyle"], uu5pluginDef.getIn(["toEditorItemDefs", "0", "style"]))
  .setIn(["toEditorItemDefs", "1", "tagName"], "span")
  .setIn(["toEditorItemDefs", "2", "tagName"], "span")
  .setIn(["toEditorItemDefs", "2", "inlineStyle"], uu5pluginDef.getIn(["toEditorItemDefs", "1", "style"]))
  .setIn(["toEditorItemDefs", "3", "tagName"], "span")
  .setIn(["toEditorItemDefs", "4", "name"], uu5pluginDef.getIn(["toEditorItemDefs", "0", "name"]))
  .setIn(["toEditorItemDefs", "4", "tagName"], "sub")
  .setIn(["toEditorItemDefs", "5", "name"], uu5pluginDef.getIn(["toEditorItemDefs", "1", "name"]))
  .setIn(["toEditorItemDefs", "5", "tagName"], "sup")
  .set(
    "toObjectDefs",
    Immutable.fromJS([
      { name: uu5pluginDef.getIn(["toEditorItemDefs", "0", "name"]), tagName: "sub" },
      { name: uu5pluginDef.getIn(["toEditorItemDefs", "2", "name"]), tagName: "sup" }
    ])
  );

export default pluginDef;
