import uu5pluginDef from "../uu5/italic.js";

const pluginDef = uu5pluginDef
  .delete("toObjectDefs")
  .deleteIn(["toEditorItemDefs", "1"])
  .deleteIn(["toEditorItemDefs", "2"])
  .deleteIn(["toEditorItemDefs", "3"])
  .setIn(["toEditorItemDefs", "0", "tagName"], "i")
  .setIn(["toEditorItemDefs", "1", "tagName"], "em")
  .setIn(["toEditorItemDefs", "0", "inlineStyle"], uu5pluginDef.getIn(["toEditorItemDefs", "1", "style"]));

export default pluginDef;
