import uu5pluginDef from "../uu5/unstyled.js";

import EditorBlockLazy from "../../editor-block-lazy.js";

let pluginDef = uu5pluginDef
  .setIn(["toObjectDefs", "tagName"], "div")
  .setIn(["toEditorItemDefs", "0", "tagName"], "div")
  .setIn(["toEditorItemDefs", "0", "component"], properties => {
    return (
      <div {...properties.blockProps} className="uu5-common-div">
        <EditorBlockLazy {...properties} />
      </div>
    );
  });

export default pluginDef;
