import * as UU5 from "uu5g04";

import Immutable from "immutable";

const PLUGIN_NAME = "code";

const pluginDef = Immutable.Map(
  Immutable.fromJS({
    name: PLUGIN_NAME,
    type: "entity",
    buttonDefs: {
      name: "code",
      icon: "mdi-code-tags",
      label: () => UU5.Environment.Lsi.RichText.plugins.code.label
    },
    toEditorItemDefs: {
      tagName: "UU5.Bricks.Code",
      htmlAliases: [
        { tagName: "code" },
        {
          tagName: "span",
          style: {
            color: "rgb(211, 47, 47)",
            fontFamily: "monospace, monospace",
            backgroundColor: "rgb(255, 235, 238)"
          }
        }
      ],
      component: properties => {
        const dataProps = properties.contentState.getEntity(properties.entityKey).getData();
        return <UU5.Bricks.Code {...dataProps}>{properties.children}</UU5.Bricks.Code>;
      }
    }
  })
);

export default pluginDef;
