import * as UU5 from "uu5g04";

import Immutable from "immutable";

import EditorBlockLazy from "../../editor-block-lazy.js";

const PLUGIN_NAME = "p";

const pluginDef = Immutable.Map(
  Immutable.fromJS({
    name: PLUGIN_NAME,
    type: "block",
    buttonDefs: {
      name: "paragraph",
      icon: "mdi-format-paragraph",
      label: () => UU5.Environment.Lsi.RichText.plugins.p.label
    },
    toEditorItemDefs: {
      tagName: "UU5.Bricks.P",
      component: properties => {
        return (
          <UU5.Bricks.Div
            {...properties.blockProps}
            className={"uu5-bricks-paragraph " + (properties.blockProps.className || "")}
          >
            <EditorBlockLazy {...properties} />
          </UU5.Bricks.Div>
        );
      },
      htmlAliases: [{ tagName: "p", style: true }, { tagName: "div", className: /uu5-bricks-paragraph/, style: true }]
    }
  })
);

export default pluginDef;
