import * as UU5 from "uu5g04";

import Immutable from "immutable";

import EditorBlockLazy from "../../editor-block-lazy.js";

const PLUGIN_NAME = "paragraph";

const pluginDef = Immutable.Map(
  Immutable.fromJS({
    name: PLUGIN_NAME,
    type: "block",
    toEditorItemDefs: {
      name: "paragraph",
      tagName: "UU5.Bricks.Paragraph",
      component: properties => {
        return (
          <UU5.Bricks.Div
            {...properties.blockProps}
            className={"uu5-bricks-paragraph " + (properties.blockProps.className || "")}
          >
            <EditorBlockLazy {...properties} />
          </UU5.Bricks.Div>
        );
      }
    }
  })
);

export default pluginDef;
