import Immutable from "immutable";

const PLUGIN_NAME = "save";

const pluginDef = Immutable.Map(
  Immutable.fromJS({
    name: PLUGIN_NAME,
    type: "custom",
    commands: {
      keyCode: 83, // s
      fn: () => {
        // only log saving. There is no way how to call save on any component
        // eslint-disable-next-line no-console
        console.log("Save");
        return true;
      }
    }
  })
);

export default pluginDef;
