import UU5 from "uu5g04";
import Immutable from "immutable";

const PLUGIN_NAME = "underline";
const STYLE_NAME = "UNDERLINE";

const pluginDef = Immutable.Map(
  Immutable.fromJS({
    name: PLUGIN_NAME,
    type: "inline",
    buttonDefs: {
      name: "underline",
      icon: "mdi-format-underline",
      label: () => UU5.Environment.Lsi.RichText.plugins.underline.label,
      value: STYLE_NAME
    },
    toEditorItemDefs: [
      {
        name: STYLE_NAME,
        tagName: "UU5.Bricks.U",
        htmlAliases: [{ tagName: "span", style: { textDecoration: /underline/ } }, { tagName: "u" }]
      },
      {
        name: STYLE_NAME,
        tagName: "UU5.Bricks.Span",
        style: { textDecoration: "underline" }
      },
      {
        name: STYLE_NAME,
        tagName: "u"
      }
    ],
    toObjectDefs: {
      name: STYLE_NAME,
      type: "inline",
      tagName: "u"
    }
  })
);

export default pluginDef;
