"use strict";

const NAMESPACES = {};

class ClsHooked {
  static getNamespace(name) {
    return NAMESPACES[name];
  }

  static createNamespace(name) {
    let namespace = new Namespace();
    NAMESPACES[name] = namespace;
    return namespace;
  }
}

class Namespace {
  constructor() {
    this.active = {};
  }

  set(name, value) {
    this.active[name] = value;
  }

  get(name) {
    return this.active[name];
  }

  run(fn) {
    fn();
    return this;
  }

  bindEmitter(emitter) {
    // Empty mock method.
  }
}

module.exports = ClsHooked;
