class ExecutionQueue {

  constructor() {
    this.queue = [];
  }

  addEvent(event) {
    this.queue.push(event);
  }

  async execute(clear = false) {
    for (let event of this.queue) {
      await event();
    }
    if (clear) {
      this.clear();
    }
  }

  clear() {
    this.queue = [];
  }

}

module.exports = ExecutionQueue;
