const path = require("path");

module.exports = class PostInstall {
  constructor(config) {
    this.config = config;
  }
  async process() {
    // only continue this task if we're installed as dependency of something
    if (path.basename(path.resolve(__dirname, "..", "..", "..")) !== "node_modules") return;

    const ProjectUpdate = require("./project-update.js");
    ProjectUpdate.showWarnings();
  }
};
