const helpers = require("../tools/helpers.js");

module.exports = class Undeploy {

  constructor(config) {
    this.config = config;
  }

  async process() {
    console.log("Undeploying application.");
    let UuCloudUnshare = helpers.requireByTemplateType(__dirname + "/uu-cloud-unshare.js");
    await new UuCloudUnshare(this.config).process();
    let UuCloudUndeploy = helpers.requireByTemplateType(__dirname + "/uu-cloud-undeploy.js");
    await new UuCloudUndeploy(this.config).process();
  }

};
