"use strict";

const Token = require("jsonwebtoken");
const GetPem = require("rsa-pem-from-mod-exp");

class Jwt {

  static async decode(token, opts = {}) {
    return Token.decode(token, opts);
  }

  static async verify(token, publicKeyData, opts = {}) {
    let publicKey = GetPem(publicKeyData.n, publicKeyData.e);
    Token.verify(token, publicKey, opts);
  }

}

module.exports = Jwt;
