//@@viewOn:imports
import UU5 from "uu5g04";
import { createVisualComponent } from "uu5g04-hooks";

import Cfg from "../core/_config.js";
import Lsi from "../config/lsi.js";
//@@viewOff:imports

//@@viewOn:statics
const STATICS = {
  displayName: Cfg.tag("Bricks.IconPickerInput")
};
//@@viewOff:statics

const IconPickerInput = createVisualComponent({
  ...STATICS,

  //@@viewOn:propTypes
  propTypes: {
    componentProps: UU5.PropTypes.object,
    onChangeProps: UU5.PropTypes.func,
    disable: UU5.PropTypes.bool,
    label: UU5.PropTypes.object,
    iconProps: UU5.PropTypes.string,
    openToContent: UU5.PropTypes.bool
  },
  //@@viewOff:propTypes

  //@@viewOn:defaultProps
  defaultProps: {
    componentProps: undefined,
    onChangeProps: undefined,
    disabled: false,
    label: Lsi.icon,
    iconProps: "icon",
    openToContent: true
  },
  //@@viewOff:defaultProps

  render(props) {
    //@@viewOn:hooks
    //@@viewOff:hooks

    //@@viewOn:interface
    //@@viewOff:interface

    //@@viewOn:private
    //@@viewOff:private

    //@@viewOn:render
    return (
      <UU5.Forms.IconPicker
        {...UU5.Common.VisualComponent.getAttrs(props)}
        size="m"
        label={props.label}
        value={props.componentProps[props.iconProps]}
        onChange={opt => props.onChangeProps({ [props.iconProps]: opt.value })}
        openToContent={true}
        categories={["mdi", "glyphicon", "fa", "uu5", "plus4u5"]}
      />
    );
    //@@viewOff:render
  }
});

export default IconPickerInput;
