import UU5 from "uu5g04";

export default {
  getLsiComponent: function(lsiObj, params) {
    let out;
    if (typeof lsiObj === "string") {
      out = lsiObj;
    } else {
      if (params) {
        let lsiResult = {};
        for (let lang in lsiObj) {
          for (let replaceKey in params) {
            lsiResult[lang] = lsiObj[lang].replace(`\${${replaceKey}}`, params[replaceKey]);
          }
        }
        out = <UU5.Bricks.Lsi lsi={lsiResult} />;
      } else {
        out = <UU5.Bricks.Lsi lsi={lsiObj} />;
      }
    }
    return out;
  }
};
